INSERT INTO `camnghi` VALUES ('5', 'Trịnh Nguyễn Minh Tâm', 'CNXN K45', 'Hành trình bốn năm học tại trường đã cho em nhiều bài học, kinh nghiệm quý báu trong cuộc sống. Đó là hành trang cho tương lai mai sau. Em xin chân thành cảm ơn quý thầy cô, gia đình và bạn bè.', '1953070072.', '1953070072');
INSERT INTO `camnghi` VALUES ('6', 'Nguyễn Yến Duy', 'XN45', 'Con cảm thấy rất biết ơn cha mẹ đã cho con cơ hội đi học, và làm việc theo đúng mong ước của mình. Con yêu ba mẹ rất nhiều!!!', '1953070021.', '1953070021');
