INSERT INTO `camnghi` VALUES ('319', 'Bùi Nguyễn Ngọc Trân', 'YHCT K43', 'Những ký ức về CTUMP thật đáng nhớ, những ký ức ấy sẽ luôn sống mãi trong em và sẽ là những mảnh ghép thanh xuân đẹp nhất của em.', '1753080085.', '1753080085');
